// convert path to absolute if relative
function derelativizePath(path) {
    var f = new java.io.File(path);
    if(!f.exists()) {
        f = new java.io.File(new String(project.getBaseDir()) + S + path);
    }
    if(f.exists()) {
        try {
            return f.getCanonicalPath();
        } catch(err) {
            return path;
        }
    }
    return path;
}

