while(keys.hasMoreElements()) {
    var pn = new String(keys.nextElement());
    if(startsWith(pn, JFXCALLB)) {
        var prop = new String(project.getProperty(pn));
        if(defined(prop)) {
            var cname = pn.substring(JFXCALLB.length+1);
